import { Component, Input, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';

import { AlbumDetail } from '../albumdetail';
import { AlbumService } from '../album.service';
import { ThrowStmt } from '@angular/compiler';

@Component({
  selector: 'app-album-detail',
  templateUrl: './album-detail.component.html'
})

export class AlbumDetailComponent implements OnInit {
  @Input() albumDetail: AlbumDetail;
  addCommentSelected = false;

  constructor(
    private route: ActivatedRoute,
    private albumService: AlbumService
  ) {
  }

  albumId : number;

  getAlbumDetail() : void {
    this.albumService.getAlbumDetail(this.albumId).subscribe(albumDetail => {
      this.albumDetail = albumDetail;
    });
  }

  ngOnInit() {
    if (this.albumDetail === undefined) {
      this.albumId = +this.route.snapshot.paramMap.get('id');
      this.getAlbumDetail();
    }
  }

  activateAddComment(activate: boolean): void {
    this.addCommentSelected = activate;
  }
}
<div *ngIf="this.albumDetail" class="container bg-white px-3 py-3 rounded my-3">
  <h1>{{ albumDetail.name }}</h1>
  <hr>
  <div class="row">
    <div class="col-3">
      <div class="thumb">
        <img class="img-fluid" src="{{ albumDetail.cover }}" alt="Imagen de {{ albumDetail.name }}" />
      </div>
    </div>

    <div class="col bg-white">
      <div class="card-body">
        <h2 i18n>Artist</h2>
        <p *ngFor="let p of albumDetail.performers">{{p.name}}</p>
        <h2 i18n>Release Date</h2>
        <p>{{ albumDetail.releaseDate | date: "longDate" }}</p>
        <h2 i18n>Genre</h2>
        <p>{{ albumDetail.genre }}</p>
        <h2 i18n>Record Label</h2>
        <p>{{ albumDetail.recordLabel }}</p>
        <hr />
        <h2 i18n>Description</h2>
        <p>{{ albumDetail.description }}</p>
        <hr />
        <h2 i18n>Tracks</h2>
        <div *ngFor="let t of albumDetail.tracks; let i = index">
          {{ i + 1 }}- <strong> {{ t.name }} </strong> :
          <em>({{ t.duration }})</em>
        </div>
      </div>
    </div>
  </div>

  <div class="row">
    <div class="col bg-white">
      <div class="container">
        <hr>

        <div class="row">
          <h2 class="col-9" i18n>Comments</h2>
          <div class="col-3">
            <button id="new-comment" style="float: right;" class="btn btn-dark" (click)="activateAddComment(true)" i18n>
              Add comment
            </button>
          </div>
        </div>

        <div class="card" *ngIf="this.addCommentSelected">
          <div class="card-body">
            <app-comment-creator [albumDetail]="this.albumDetail" (returnEvent)="activateAddComment(false)">
            </app-comment-creator>
          </div>
        </div>
        <br>

        <div *ngFor="let c of albumDetail.comments">

          <div class="card" >
            <div class="card-body">
              <strong>Rating: {{ c.rating }}</strong>
              <br>
              <p>{{c.description}}</p>
            </div>
          </div>
          <br>
        </div>
      </div>
    </div>
  </div>
</div>
