import { Component, OnInit } from '@angular/core';
import { BandService } from '../band.service';
import { BandDetail } from '../band-detail';

@Component({
  selector: 'app-musician-list',
  templateUrl: './band-list.component.html',
  styleUrls: ['./band-list.component.css']
})
export class BandListComponent implements OnInit {

  bands: BandDetail[] = [];
  filteredBands: BandDetail[] = [];

  selectedBand: BandDetail;
  selected = false;

  constructor(private bandService: BandService) {
  }

  getBands(): void {
    this.bandService.getBands()
      .subscribe(musicians => {
        this.bands = musicians;
        this.filteredBands = musicians;
      });
  }

  ngOnInit() {
    this.getBands();
  }

  filterBands(text: string){
    text = text == null ? "" : text.trim().toLowerCase();
    if(text == ""){
      this.filteredBands = this.bands;
    }else{
      this.filteredBands = this.bands.filter(m => m.name.toLowerCase().match(text));
    }
  }

  onSelectedBand(m: BandDetail): void {
    this.bandService.getBandDetail(m.id)
      .subscribe(band => {
        if(band !== null){
          this.selectedBand = band;
          this.selected = true;
        }
      });
  }

  unselectBand(selected: boolean){
    this.selected = selected;
  }
}
<div *ngIf="selected" class="container-fluid bg-light px-5">
  <app-band-detail
    [bandDetail]="selectedBand"
    (returnEvent)="unselectBand(event)"
  ></app-band-detail>
</div>

<div *ngIf="!selected" class="container-fluid bg-light px-5 py-3">
  <h1 class="container" i18n>Bands</h1>

  <div class="container bg-white px-3 py-3 rounded">
    <div style="float: right;">
      <input class="form-control" (input)="filterBands($event.target.value)" placeholder="Search" i18n-placeholder>
    </div>
    <br>
    <br>
    <div *ngFor="let band of filteredBands">
      <div class="card mb-3">
        <div class="row g-0">
          <div class="col-md-2 d-flex">
            <img
              [src]="band.image"
              (click)="onSelectedBand(band)"
              class="m-auto"
              alt="{{band.name+'.image'}}"
              width="150px"
              style="cursor: pointer;"
            />
          </div>
          <div class="col-md-10">
            <div class="card-body">
              <h2 class="card-title" style="cursor: pointer;" (click)="onSelectedBand(band)">{{ band.name }}</h2>
              <p class="card-text">{{ band.description }}</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>
