import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ToastrService } from 'ngx-toastr';
import { MusicianService } from '../musician.service';
import { Band } from "../../band/band"

@Component({
  selector: 'app-musicianband-create',
  templateUrl: './musicianband-create.component.html',
})
export class MusicianbandCreateComponent implements OnInit {
  @Input() musicianId: number;
  @Output() returnEvent = new EventEmitter<boolean>();
  musicianBandForm: FormGroup;
  bandas: Band[]

  constructor(
    private formBuilder: FormBuilder,
    private toastr: ToastrService,
    private musicianService: MusicianService
  ) {}

  ngOnInit(): void {
    this.musicianService.getBandas().subscribe((bandas) => {
      this.bandas = bandas;
    })
    this.musicianBandForm = this.formBuilder.group({
      band: ['', Validators.required]
    });

  }

  createMusicianBand(bandObject: any) {
    this.showSuccess(bandObject);
    this.musicianService.addMusicianToBand(this.musicianId, bandObject.band ).subscribe(
      response => { console.log(response); this.emitReturnEvent(); }
    )

  }

  showSuccess(c: any) {
    this.toastr.success('Creado exitosamente!', `Musico`, {
      progressBar: true,
      timeOut: 4000,
    });
  }

  cancelCreation() {
    console.log('Cancelando ...');
    this.musicianBandForm.reset();
    this.emitReturnEvent();
  }

  emitReturnEvent() {
    this.returnEvent.emit(false);
  }
}
<div class="container-fluid">
  <div class="col p-3">
    <div class="h3" i18n>Add musician to a band</div>
    <form
      [formGroup]="musicianBandForm"
      (ngSubmit)="
        !musicianBandForm.invalid && createMusicianBand(musicianBandForm.value)
      "
    >
      <div class="form-group mx-sm-3 mb-2">
        <label for="banda"> Banda* </label>
        <select formControlName="band" id="banda" class="form-select">
          <option *ngFor="let banda of bandas" [value]="banda.id">
            {{ banda.name }}
          </option>
        </select>

        <div
          class="alert alert-danger alert-dismissible fade show"
          *ngIf="
          musicianBandForm.get('band').hasError('required') &&
          musicianBandForm.get('band').touched
          "
        >
          Banda Requerida
        </div>
      </div>

      <div class="row m-3 small">
        <p>* Campos obligatorios</p>
      </div>

      <div class="row m-3">
        <button
          type="submit"
          class="col btn btn-success"
          [disabled]="!musicianBandForm.valid"
        >
          Create
        </button>
        <button
          type="button"
          class="col btn btn-danger ml-3"
          (click)="cancelCreation()"
        >
          Cancel
        </button>
      </div>
    </form>
  </div>
</div>
