import { Component, EventEmitter, OnInit, Output } from '@angular/core';
import { Router } from '@angular/router';
import { Collector } from '../collector';
import { CollectorDetail } from '../collector-detail';
import { CollectorService } from '../collector.service';

@Component({
  selector: 'app-collector-list',
  templateUrl: './collector-list.component.html'
})
export class CollectorListComponent implements OnInit {
  collectors: CollectorDetail[];

  selectedCollector: CollectorDetail;
  selected = false;

  constructor(private collectorService: CollectorService, private router: Router) { }

  getCollectors(): void {
    this.collectorService.getCollectors().subscribe((collectors) => {
      this.collectors = collectors;
    });
  }

  getFavoritePerformersName(collector: CollectorDetail): string[] {
    return collector.favoritePerformers.map(e => e.name);
  }

  onSelectedCollector(collector: Collector): void {
    this.collectorService.getCollectorDetail(collector.id).subscribe(
      (collectorDetail) => {
        this.selectedCollector = collectorDetail;
        this.selected = true;
      })
    this.router.navigate(['collectors',collector.id])
  }

  unselectCollector(selected: boolean){
    this.selected = selected;
  }

  ngOnInit(): void {
    this.getCollectors();
  }
}
<div *ngIf="selected" class="container-fluid bg-light px-5">
  <app-collector-detail
    [collectorDetail]="selectedCollector"
    (returnEvent)="unselectCollector(event)"
  ></app-collector-detail>
</div>

<div *ngIf="!selected" class="container-fluid bg-light px-5 py-3">
  <h1 class="container">Coleccionistas</h1>
  <div class="container bg-white px-3 py-3 rounded">
    <div
      class="card mb-3 rounded border-bottom"
      *ngFor="let collector of collectors"
    >
      <div class="row g-0">
        <div class="col-md-11">
          <div class="card-body">
            <h2 class="card-title">{{ collector.name }}</h2>
            <p class="card-text text-secondary">
              <span style="font-weight: 500 !important">
                Álbumes disponibles:
              </span>
              {{ collector.collectorAlbums.length }}
            </p>
            <p class="card-text text-secondary">
              <span style="font-weight: 500 !important">
                Artistas favoritos:
              </span>
              <ng-container
                *ngFor="
                  let item of collector.favoritePerformers;
                  let i = index;
                  last as isLast
                "
              >
                <ng-container *ngIf="i < 3">
                  <ng-container *ngIf="!isLast">{{ item.name }},</ng-container>
                  <ng-container *ngIf="isLast">{{ item.name }}.</ng-container>
                </ng-container>
              </ng-container>
            </p>
          </div>
        </div>
        <div
          class="col-12 col-md-1 container-fluid align-self-center p-2 justify-content-center d-flex"
        >
          <button
            type="button"
            class="d-none d-md-flex btn btn-outline-dark rounded-pill w-auto"
            (click)="onSelectedCollector(collector)"
          >
            <span class="material-icons md-24"> chevron_right </span>
          </button>
          <button
            type="button"
            class="btn btn-outline-dark rounded-pill d-md-none w-75"
            (click)="onSelectedCollector(collector)"
          >
            <span class="material-icons md-24"> chevron_right </span>
          </button>
        </div>
      </div>
    </div>
  </div>
</div>
