import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from "@angular/forms";
import { ToastrService } from "ngx-toastr";
import { Album } from '../album';
import { AlbumService } from "../album.service";
import { RECORD_LABEL } from 'src/app/recordlabel/recordlabel.enum';
import { GENRE } from "src/app/genre/genre.enum";
import { HttpErrorResponse } from '@angular/common/http';
import { Track } from 'src/app/track/track';
import { AlbumDetail } from '../albumdetail';


@Component({
  selector: 'app-album-track-create',
  templateUrl: './album-track-create.component.html'
})
export class AlbumTrackCreateComponent implements OnInit {
  @Output() reloadAlbums: EventEmitter<void> = new EventEmitter<void>();
  //@Input() albumId: number;
  trackForm: FormGroup;
  albums: AlbumDetail[];
  selected: Album;

  constructor(
    private formBuilder: FormBuilder,
    private toastr: ToastrService,
    private albumService: AlbumService,
  ) { }

  createTrack(newTrack: Track) {
    this.albumService.addNewTrack(newTrack)
      .subscribe(response => {
        this.emitReturnEvent();
        this.reloadAlbums.emit();
        this.showSuccess(newTrack);
      }, (error) => {
        this.showError(newTrack, error);
      });
    this.trackForm.reset();
    console.log(this.selected);
  }

  showSuccess(c: Track) {
    this.toastr.success('Creado exitosamente!', `Track ${c.name}`, { "progressBar": true, timeOut: 4000 });
  }

  showError(c: Track, error: HttpErrorResponse) {
    this.toastr.success('Error creando track!', `Track ${c.name} ${error.error.message}`, { "progressBar": true, timeOut: 4000 });
  }

  cancelCreation() {
    this.trackForm.reset();
    //this.emitReturnEvent();
  }

  emitReturnEvent() {
    this.reloadAlbums.emit();
  }

  ngOnInit() {
    this.albumService.getAlbums().subscribe((albums) => {
      this.albums = albums;
    });

    this.trackForm = this.formBuilder.group({
      name: ["", [Validators.required, Validators.minLength(1)]],
      duration: ["", [Validators.required, Validators.minLength(1), Validators.maxLength(5), Validators.pattern("[0-5][0-9]:[0-5][0-9]")]],
      id: ["", [Validators.required]]
    })
  }
}
<div class="container bg-white px-3 py-3 rounded my-3">
  <h1 i18n>Create and assign track to album</h1>
  <hr />
  <div class="row">
    <form
      [formGroup]="trackForm"
      (ngSubmit)="!trackForm.invalid && createTrack(trackForm.value)"
    >
      <div class="form-group">
        <label for="name" i18n> Track Name* </label>

        <input
          novalidate
          id="name"
          class="form-control"
          formControlName="name"
          placeholder="track name"
          i18n-placeholder
        />

        <div
          class="alert alert-danger alert-dismissible fade show"
          *ngIf="
            trackForm.get('name').hasError('required') &&
            trackForm.get('name').touched
          "
          i18n
        >
          A track name is required
        </div>

        <div
          class="alert alert-danger alert-dismissible fade show"
          *ngIf="
            trackForm.get('name').hasError('minlength') &&
            trackForm.get('name').touched
          "
          i18n
        >
          Track name too short.
        </div>
      </div>

      <div class="form-group">
        <label for="duration" i18n>Track duration (mm:ss)*</label>

        <input
          novalidate
          type="string"
          id="duration"
          class="form-control"
          formControlName="duration"
          placeholder="mm:ss"
          i18n-placeholder
        />

        <div
          class="alert alert-danger alert-dismissible fade show"
          *ngIf="
            trackForm.get('duration').hasError('required') &&
            trackForm.get('duration').touched
          "
          i18n
        >
          Track duration is required
        </div>

        <div
          class="alert alert-danger alert-dismissible fade show"
          *ngIf="
            trackForm.get('duration').hasError('minlength') &&
            trackForm.get('duration').touched
          "
          i18n
        >
          Track duration is too short.
        </div>

        <div
          class="alert alert-danger alert-dismissible fade show"
          *ngIf="
            trackForm.get('duration').hasError('pattern') &&
            trackForm.get('duration').touched
          "
          i18n
        >
          Track duration does not match the format [mm:ss]
        </div>

        <div
          class="alert alert-danger alert-dismissible fade show"
          *ngIf="
            trackForm.get('duration').hasError('maxlength') &&
            trackForm.get('duration').touched
          "
          i18n
        >
          Track duration has too many characters.
        </div>
      </div>

      <div class="form-group">
        <label for="id" i18n> Assign new track to album* </label>
        <select
          id="id"
          class="form-select"
          formControlName="id"
          aria-label=".form-select-sm example"
        >
          <option selected i18n>
            Select an album to assign the track created &#9662;
          </option>
          <option *ngFor="let album of albums" [value]="album.id">
            {{ album.name }}
          </option>
        </select>

        <div
          class="alert alert-danger alert-dismissible fade show"
          *ngIf="
            trackForm.get('id').hasError('required') &&
            trackForm.get('id').touched
          "
          i18n
        >
          An album is required to be selected
        </div>
      </div>

      <div class="form-group" style="float: right">
        <button
          type="button"
          id="track_cancel"
          class="btn btn-danger"
          (click)="cancelCreation()"
          i18n
        >
          Cancel
        </button>
        &nbsp;
        <button
          type="submit"
          id="track_create_assign"
          class="btn btn-primary"
          [disabled]="!trackForm.valid"
          i18n
        >
          Create & Assign
        </button>
      </div>
    </form>
  </div>
</div>
