import { Component, OnInit } from '@angular/core';
import { AlbumDetail } from '../albumdetail';
import { AlbumService } from '../album.service';
import { AlbumCreateComponent } from "../album-create/album-create.component";
import { AlbumTrackCreateComponent } from "../album-track-create/album-track-create.component";

@Component({
  selector: 'app-album-list',
  templateUrl: './album-list.component.html',
  styleUrls: ['./album-list.component.css']
})
export class AlbumListComponent implements OnInit {
  public albums: AlbumDetail[];
  selectedAlbum: AlbumDetail;
  selected = false;
  activateAlbumCreation = false;
  activateTrackCreation = false;

  constructor(private albumService: AlbumService) { }

  ngOnInit(): void {
    this.getAlbums();
  }

  getAlbums(): void {
    this.albumService.getAlbums().subscribe(albums => this.albums = albums);
  }

  createNewAlbum(activate: boolean): void {
    this.activateAlbumCreation = activate;
    this.selected = !activate;
    this.activateTrackCreation = false;
  }

  createNewTrack(activate: boolean): void {
    this.activateTrackCreation = activate;
    this.selected = !activate;
    this.activateAlbumCreation = false;
  }

  onSelectedAlbum(a: AlbumDetail): void {
    this.albumService.getAlbumDetail(a.id)
      .subscribe(album => {
        if (album !== null) {
          this.selectedAlbum = album;
          this.selected = true;
          this.activateAlbumCreation = false;
        }
      });
  }

}
<div class="container">
  <div class="row">
    <h1 class="col-9" i18n>Albums</h1>

    <div class="col-3" style="float: right;">
      <button
        id="add_album"
        type="button"
        class="btn btn-dark"
        (click)="createNewAlbum(true)"
        i18n
      >
        Add album
      </button>
      &nbsp;
      <button
        type="button"
        class="btn btn-dark"
        (click)="createNewTrack(true)"
        i18n
      >
        Add tracks
      </button>
    </div>
  </div>

  <div class="row">
    <div
      *ngFor="let album of albums"
      class="col-sm-12 col-md-6 col-lg-3 hover-cursor"
    >
      <div class="card" style="width: 17rem">
        <img
          class="card-img-top"
          src="{{ album.cover }}"
          (click)="onSelectedAlbum(album)"
          alt="Cubierta de {{ album.cover }}"
          height="250px"
        />
        <div class="card-body">
          <h2 class="card-title" (click)="onSelectedAlbum(album)">
            {{ album.name }}
          </h2>
        </div>
      </div>
    </div>
  </div>
</div>

<div *ngIf="this.selected && !this.activateAlbumCreation">
  <app-album-detail [albumDetail]="selectedAlbum"></app-album-detail>
</div>
<div *ngIf="this.activateAlbumCreation && !this.selected">
  <app-album-create (reloadAlbums)="getAlbums()"></app-album-create>
</div>
<div *ngIf="this.activateTrackCreation && !this.selected">
  <app-album-track-create (reloadAlbums)="getAlbums()"></app-album-track-create>
</div>
