  constructor(formBuilder, toastr, albumService) {
    this.formBuilder = formBuilder;
    this.toastr = toastr;
    this.albumService = albumService;
    this.reloadAlbums = new EventEmitter();
    this.recordLabels = [];
    this.genres = [];
  }

  createAlbum(newAlbum) {
    this.albumService.addNewAlbum(newAlbum).subscribe(response => {
      this.emitReturnEvent();
      this.reloadAlbums.emit();
      this.showSuccess(newAlbum);
    }, error => {
      this.showError(newAlbum, error);
    });
    this.albumForm.reset();
  }

  showSuccess(c) {
    this.toastr.success('Creado exitosamente!', `Album ${c.name}`, {
      "progressBar": true,
      timeOut: 4000
    });
  }

  showError(c, error) {
    this.toastr.success('Error creando album!', `Album ${c.name} ${error.error.message}`, {
      "progressBar": true,
      timeOut: 4000
    });
  }

  cancelCreation() {
    this.albumForm.reset();
    this.emitReturnEvent();
  }

  emitReturnEvent() {
    this.reloadAlbums.emit();
  }

  ngOnInit() {
    this.albumForm = this.formBuilder.group({
      name: ["", [Validators.required, Validators.minLength(1)]],
      cover: ["", [Validators.required, Validators.minLength(1)]],
      releaseDate: ["", [Validators.required]],
